INSERT INTO `ecs_article` VALUES ('20', '8', '合作加盟', '<h2><span style=\"font-size: smaller\">&nbsp; 加入彩壳网即可拥有四大优势:</span></h2>\r\n<h2><strong><span style=\"font-size: smaller\">&nbsp; </span></strong><span style=\"font-size: smaller\">平台优势；资源优势；政策优势；服务优势；<br />\r\n</span><strong><span style=\"font-size: smaller\">&nbsp;&nbsp;<br />\r\n&nbsp; </span></strong><span style=\"font-size: smaller\">平台优势</span></h2>\r\n<p><span style=\"font-size: smaller\">&nbsp;</span><strong><span style=\"font-size: smaller\">&nbsp;&nbsp;&nbsp;</span></strong><span style=\"font-size: smaller\">&nbsp;彩壳网位于深圳，创始人从事于通讯产品10几年对于产品的生产链非常熟悉。于1000多家知名品牌工厂一起合作。所有的价格都是工厂直供，经彩壳网验收，直接配送到全国各地。&nbsp;</span></p>\r\n<p>&nbsp;</p>\r\n<h2><span style=\"font-size: smaller\">&nbsp;<strong>资源优势<br />\r\n</strong>&nbsp;&nbsp;&nbsp; </span></h2>\r\n<p><span style=\"font-size: smaller\">一站式采购的专业平台，使加入彩壳网的采购商既可以一次性完成采购，巨大的采购大大降低了产品的价格。实现厂家与加盟商的双赢。</span></p>\r\n<p>&nbsp;</p>\r\n<h2><span style=\"font-size: smaller\">&nbsp; 政策优势</span></h2>\r\n<p><span style=\"font-size: smaller\"><br />\r\n1、有实体的加盟商还可以得到店面的装修设计支持，包括柜台，海报，门头等。</span></p>\r\n<p><span style=\"font-size: smaller\">2、加盟商下单一个月内，如产品滞销。在不影响第2次销售的情况下可申请换货。</span></p>\r\n<p>&nbsp;</p>\r\n<h2><span style=\"font-size: smaller\">&nbsp; 服务优势</span></h2>\r\n<p><span style=\"font-size: smaller\"><br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 合作的加盟商免费建立企业的公众账号；并协助建立企业的微商城；企业的官网。让您比同行领先一步提前进入移动互联网实现企业与消费者的直接对接，通过线上与线下相结合形成地域的销售垄断。实现真正的o2o.</span></p>\r\n<p><span style=\"font-size: smaller\">不定期的提供产品的培训，销售培训，电子商务培训；o2o营销模式的培训；彩壳网竭能帮助企业一臂之力，早日实现双赢！</span></p>\r\n<p>&nbsp;</p>\r\n<h2><span style=\"font-size: smaller\">加盟敬请联系:陆小姐，微信：ruby_1112<br />\r\n手机：13607869799</span></h2>\r\n<p>&nbsp;</p>', '', '', '', '0', '1', '1381083304', '', '0', 'http://', '');
