INSERT INTO `ecs_goods` VALUES ('350', '10', 'ECS000350', '浦诺菲 BH618蓝牙耳机 立体声音乐蓝牙耳机 全新蓝牙3.0版本 银色', null, '+', '46', '0', '', '1', '0.000', '103.20', '86.00', '0.00', '0', '0', '1', '', '', '<div><span style=\"font-size: larger\"><span style=\"font-family: Arial\"><font size=\"4\">一、产品外观</font></span></span></div>\r\n<div><span style=\"font-size: larger\"><span style=\"font-family: Arial\"><font size=\"4\">精致外观，典雅造型，彰显时尚商务气质</font></span></span></div>\r\n<div><span style=\"font-size: larger\"><span style=\"font-family: Arial\"><font size=\"4\"><img alt=\"\" src=\"http://www.pivoful.com/v15/Upload/2013641518161600.jpg\" width=\"440\" height=\"280\" /></font></span></span></div>\r\n<div><span style=\"font-size: larger\"><span style=\"font-family: Arial\"><font size=\"4\">二、产品特点：</font></span></span></div>\r\n<div><span style=\"font-size: larger\"><span style=\"font-family: Arial\"><font size=\"4\">1.单声道蓝牙耳机给通话带来了极大的便利，但它不能听音乐，有点小遗憾。立体声蓝牙耳机支持A2DP协议，可播放音乐，但用于通话时，有了耳机线的缠绕，就显得不那么方便，而这款BH618蓝牙耳机可在单声道与立体声之间自由切换，工作娱乐两不误。通话可直接把主机挂在耳朵上当单声道耳机使用，听歌时为了达到更好的立体声效果，只需把标配的耳机线接上就成了立体声蓝牙耳机。</font></span></span></div>\r\n<div><span style=\"font-size: larger\"><span style=\"font-family: Arial\"><font size=\"4\">&nbsp;<img alt=\"\" src=\"http://www.pivoful.com/v15/Upload/2013641518456873.png\" width=\"400\" height=\"231\" /></font></span></span></div>\r\n<div><span style=\"font-size: larger\"><span style=\"font-family: Arial\"><font size=\"4\">&nbsp;</font></span></span></div>\r\n<div><span style=\"font-size: larger\"><span style=\"font-family: Arial\"><font size=\"4\">2、超强一拖二功能，即可同时连接两部手机。先将手机1与蓝牙耳机进行配对，配对成功后再用同样的方法配对手机2，连接成功后就可以让BH618同时待机两部手机了。</font></span></span></div>\r\n<div><span style=\"font-size: larger\"><span style=\"font-family: Arial\"><font size=\"4\">&nbsp;<img alt=\"\" src=\"http://www.pivoful.com/v15/Upload/2013641519177624.png\" width=\"400\" height=\"304\" /></font></span></span></div>\r\n<div><span style=\"font-size: larger\"><span style=\"font-family: Arial\"><font size=\"4\">&nbsp;</font></span></span></div>\r\n<div><span style=\"font-size: larger\"><span style=\"font-family: Arial\"><font size=\"4\">3.支持iPhone4/4S，iPhone5手机显示蓝牙耳机电池电量.</font></span></span></div>\r\n<div><span style=\"font-size: larger\"><span style=\"font-family: Arial\"><font size=\"4\"><img alt=\"\" src=\"http://www.pivoful.com/v15/Upload/2013641520124084.jpg\" width=\"400\" height=\"309\" /></font></span></span></div>\r\n<div><span style=\"font-size: larger\"><span style=\"font-family: Arial\"><font size=\"4\">&nbsp;</font></span></span></div>\r\n<div><span style=\"font-size: larger\"><span style=\"font-family: Arial\"><font size=\"4\">4，超长待机时间：BH618具有长达120个小时的续航时间和5个小时的通话时间。</font></span></span></div>\r\n<div><span style=\"font-size: larger\"><span style=\"font-family: Arial\"><font size=\"4\"><img alt=\"\" src=\"http://www.pivoful.com/v15/Upload/2013641520599611.png\" width=\"400\" height=\"240\" /></font></span></span></div>\r\n<div><span style=\"font-size: larger\"><span style=\"font-family: Arial\"><font size=\"4\">&nbsp;</font></span></span></div>\r\n<div><span style=\"font-size: larger\"><span style=\"font-family: Arial\"><font size=\"4\">三、功能说明</font></span></span></div>\r\n<div><span style=\"font-size: larger\"><span style=\"font-family: Arial\"><font size=\"4\">接听来电：来电时，耳机内发出来电提示音或铃声，短按多功能键一次接听电话，</font></span></span></div>\r\n<div><span style=\"font-size: larger\"><span style=\"font-family: Arial\"><font size=\"4\">或使用手机接听键，接听来电。</font></span></span></div>\r\n<div><span style=\"font-size: larger\"><span style=\"font-family: Arial\"><font size=\"4\">&nbsp;</font></span></span></div>\r\n<div><span style=\"font-size: larger\"><span style=\"font-family: Arial\"><font size=\"4\">末位重拔：在待机状态下长按多功能键两秒或快速按两下，实现末位重拔。</font></span></span></div>\r\n<div><span style=\"font-size: larger\"><span style=\"font-family: Arial\"><font size=\"4\">&nbsp;</font></span></span></div>\r\n<div><span style=\"font-size: larger\"><span style=\"font-family: Arial\"><font size=\"4\">拒接电话：有电话打进时长按多功能键两秒,拒接电话</font></span></span></div>\r\n<div><span style=\"font-size: larger\"><span style=\"font-family: Arial\"><font size=\"4\">&nbsp;</font></span></span></div>\r\n<div><span style=\"font-size: larger\"><span style=\"font-family: Arial\"><font size=\"4\">音乐控制：在暂停音乐时，摁一下多功能键播放音乐</font></span></span></div>\r\n<div><span style=\"font-size: larger\"><span style=\"font-family: Arial\"><font size=\"4\">&nbsp;</font></span></span></div>\r\n<div><span style=\"font-size: larger\"><span style=\"font-family: Arial\"><font size=\"4\">语音拨号：在待机状态时按多功能键2秒</font></span></span></div>', 'images/201507/thumb_img/2394_thumb_G_1436087437466.jpg', 'images/201507/goods_img/2394_G_1436087437864.jpg', 'images/201507/source_img/2394_G_1436087437158.png', '1', '', '1', '1', '0', '0', '1389781100', '100', '0', '0', '0', '0', '0', '0', '1389787270', '0', '', '-1', '-1', '0', '0', '', '', '', '');
